                }
            }
        }
        .CTAbutton {
            button {
                height: 3em;
                width: 9em;
                background-color: var(--red);
                color: var(--darkWhite);
                border: none;
                border-radius: var(--buttonBorderRadius);
                margin-bottom: 0.2em;
                cursor: pointer;
                transition-duration: var(--transitionDuration);
                &:hover {
                    transform: scale(1.01);
                    box-shadow: 2px 5px 15px -3px var(--black);
                }
            }
        }
    }
